import org.codehaus.jackson.map.annotate.JsonSerialize;

import javax.xml.bind.annotation.XmlElement;
import javax.xml.bind.annotation.XmlRootElement;


/**
 * Wrapper Entity for the output generated by Enrichment
 * @author Yorgos.Mamakis@ europeana.eu
 *
 */
@XmlRootElement
@JsonSerialize
public class EntityWrapper {

	/**
	 * Constructor for Entity Wrapper
	 * @param className The class name of the entity generated
	 * @param originalField The original field that generated this enrichment
	 * @param contextualEntity The JSON serialized contextual entity
	 */
	public EntityWrapper(String className, String originalField, String url, String originalValue,
			String contextualEntity) {
		this.className = className;
		this.originalField = originalField;
		this.contextualEntity = contextualEntity;
		this.originalValue = originalValue;
		this.url = url;
	}
	
	

	public EntityWrapper(){
		
	}
	@XmlElement
	String className;
	@XmlElement
	String originalField;
	@XmlElement
	String contextualEntity;
	@XmlElement
	String url;
	@XmlElement
	String originalValue;
	
	public String getOriginalField() {
		return originalField;
	}
	public void setOriginalField(String originalField) {
		this.originalField = originalField;
	}
	public String  getContextualEntity(){
		return contextualEntity;
	}
	
	public  void setContextualEntity(String contextualEntity){
		this.contextualEntity = contextualEntity;
	}
	public String getClassName() {
		return className;
	}
	public void setClassName(String className) {
		this.className = className;
	}

	public String getUrl() {
		return url;
	}

	public void setUrl(String url) {
		this.url = url;
	}
	
	public String getOriginalValue() {
		return originalValue;
	}

	public void setOriginalValue(String originalValue) {
		this.originalValue = originalValue;
	}
}
